import pycountry
#Creamos una pequeña funcion que busque el nombre completo del pais y apartir de ahi va a buscar en el modulo y regresa el modulo de 3 letras por pais
def get_alpha_3(location):
    try:
        return pycountry.countries.get(name=location).alpha_3
    except:
        return None #En caso de que no encuentre ningun pais regresa el valor None seria en elc aseo de World
grupo['code'] = grupo['pais'].apply(lambda x: get_alpha_3(x) )
print(grupo.head(5))
